const createController = (fileName)=>{
    const fs = require("fs")
    const path = require("path")
    let dir = path.resolve('controller')
    if(!fs.existsSync(dir+`/${fileName}.js`)){
        let content = fs.readFileSync(path.resolve(__dirname + "/controller"))
        fs.writeFileSync(dir+`/${fileName}.js`, content, function (err) {
            if (err) throw err;            
        });
        console.log('Controller is created successfully.');
    }
}

module.exports = createController